import React, { useState } from "react";
import "./../styles/EmployeeList.css";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import AddEmpModal from "./AddEmpModal";
import ShowEmpModal from "./ShowEmpModal";

import apis from "./../services/api";

function EmployeeList() {
	const [showAddEmp, setShowAddEmp] = useState(false);
	const [showEditEmp, setShowEditEmp] = useState(false);
	const [showEmp, setShowEmp] = useState(false);
	const [showIndex, setShowIndex] = useState(0);

	const [values, setValues] = useState({
		firstName: "",
		lastName: "",
		empId: "",
		emailId: "",
		contactNo: "",
	});

	const handleValueChange = (event) => {
		setValues({
			...values,
			[event.target.name]: event.target.value,
			empId: employees[employees.length - 1].empId + 1,
		});
	};

	const handleAddEmpSubmit = () => {
		console.log(JSON.stringify(values, 4, 4));
	};

	const handleEditEmpSubmit = () => {
		console.log(JSON.stringify(values, 4, 4));
	};

	async function updateEmployeesList() {
		try {
			// const obj = {
			// 	author: "",
			// 	categories: "",
			// 	dateFrom: "01-01-2012",
			// 	dateTo: "",
			// 	sortBy: sort ? data.sortByCodes[sort] : "",
			// 	descending: sort === "Latest First" ? true : false,
			// 	perPage: 5,
			// 	page: currPage ? currPage : 1,
			// 	searchString: search ? search : "",
			// 	department: department ? data.codes[department] : "",
			// };

			// console.log(JSON.stringify(obj, 4, 4));

			// let response = await apis.enrollment.getEnrollmentsByParams(obj).then((res) => res.json());
			let response = await apis.getAllEmployees().then((res) => res.json());

			console.log(response);

			if (!response.error) {
				setEnrollmentsList(response.dataArray);
				setTotalPages(response.totalPage);
			} else {
				console.log("Error in updateEnrollmentList in Enrollments.js");
				// alert(response.error);
			}
		} catch (err) {
			console.log(err, "in updateEnrollmentsList inside EnrollmentsPage");
		}
	}

	const employees = [
		{
			firstName: "Ramesh",
			lastName: "Fadatare",
			empId: 10000,
			emailId: "ram@gmail.com",
			contactNo: 1234567890,
		},
		{
			firstName: "John",
			lastName: "Cena",
			empId: 10001,
			emailId: "john@gmail.com",
			contactNo: 1234567890,
		},
		{
			firstName: "Tom",
			lastName: "Cruise",
			empId: 10002,
			emailId: "tom@gmail.com",
			contactNo: 1234567890,
		},
		{
			firstName: "Admin",
			lastName: "admin",
			empId: 10003,
			emailId: "admin@gmail.com",
			contactNo: 1234567890,
		},
	];

	return (
		<div className="EmployeeList">
			<h1>Employees List</h1>

			<Button
				variant="primary"
				className="mb-4"
				onClick={() => {
					setValues({
						firstName: "",
						lastName: "",
						empId: "",
						emailId: "",
						contactNo: "",
					});

					setShowAddEmp(true);
				}}>
				Add Employee
			</Button>

			<AddEmpModal
				headerString="Add an Employee"
				submitString="Add"
				showAddEmp={showAddEmp}
				handleAddEmpClose={() => setShowAddEmp(false)}
				values={values}
				handleValueChange={handleValueChange}
				handleAddEmpSubmit={handleAddEmpSubmit}
			/>

			<AddEmpModal
				headerString="Edit Employee"
				submitString="Edit"
				showAddEmp={showEditEmp}
				handleAddEmpClose={() => setShowEditEmp(false)}
				values={values}
				handleValueChange={handleValueChange}
				handleAddEmpSubmit={handleEditEmpSubmit}
			/>

			<ShowEmpModal show={showEmp} values={employees[showIndex]} onHide={() => setShowEmp(false)} />

			{/* Employee Table */}
			<Table striped bordered hover>
				<thead>
					<tr>
						<th>Employee First Name</th>
						<th>Employee Last Name</th>
						<th>Employee Email Id</th>
						<th>Actions</th>
					</tr>
				</thead>
				<tbody>
					{employees.map((emp) => (
						<tr key={emp.empId}>
							<td>{emp.firstName}</td>
							<td>{emp.lastName}</td>
							<td>{emp.emailId}</td>
							<td>
								<Button
									variant="info"
									className="m-1 mt-0 mb-0"
									onClick={() => {
										setValues({
											...employees[employees.indexOf(emp)],
										});

										setShowEditEmp(true);
									}}>
									Update
								</Button>
								<Button variant="danger" className="m-1 mt-0 mb-0">
									Delete
								</Button>
								<Button
									variant="info"
									className="m-1 mt-0 mb-0"
									onClick={() => {
										setShowIndex(employees.indexOf(emp));
										setShowEmp(true);
									}}>
									View
								</Button>
							</td>
						</tr>
					))}
				</tbody>
			</Table>
		</div>
	);
}

export default EmployeeList;
