import React, { useState } from "react";
import "./../styles/EmployeeList.css";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import AddEmpModal from "./AddEmpModal";

function EmployeeList() {
	const [showAddEmp, setShowAddEmp] = useState(false);
	const [showEmp, setShowEmp] = useState(false);
	const [values, setValues] = useState({
		firstName: "",
		lastName: "",
		emailId: "",
		contactNo: "",
		empId: "",
	});

	const handleAddEmpClose = () => setShowAddEmp(false);
	const handleAddEmpShow = () => setShowAddEmp(true);

	const handleValueChange = (event) => {
		setValues({
			...values,
			[event.target.name]: event.target.value,
			empId: employees[employees.length - 1].empId + 1,
		});
	};

	const handleAddEmpSubmit = () => {
		console.log(JSON.stringify(values, 4, 4));
	};

	const employees = [
		{
			firstName: "Ramesh",
			lastName: "Fadatare",
			empId: 10000,
			emailId: "ram@gmail.com",
			contactNo: 1234567890,
		},
		{
			firstName: "John",
			lastName: "Cena",
			empId: 10001,
			emailId: "john@gmail.com",
			contactNo: 1234567890,
		},
		{
			firstName: "Tom",
			lastName: "Cruise",
			empId: 10002,
			emailId: "tom@gmail.com",
			contactNo: 1234567890,
		},
		{
			firstName: "Admin",
			lastName: "admin",
			empId: 10003,
			emailId: "admin@gmail.com",
			contactNo: 1234567890,
		},
	];

	return (
		<div className="EmployeeList">
			<h1>Employees List</h1>

			<Button variant="primary" className="mb-4" onClick={handleAddEmpShow}>
				Add Employee
			</Button>

			<AddEmpModal
				showAddEmp={showAddEmp}
				handleAddEmpClose={handleAddEmpClose}
				values={values}
				handleValueChange={handleValueChange}
				handleAddEmpSubmit={handleAddEmpSubmit}
			/>

			<ShowEmpModal show={showEmp} onHide={() => setModalShow(false)} />

			{/* Employee Table */}
			<Table striped bordered hover>
				<thead>
					<tr>
						<th>Employee First Name</th>
						<th>Employee Last Name</th>
						<th>Employee Email Id</th>
						<th>Actions</th>
					</tr>
				</thead>
				<tbody>
					{employees.map((emp) => (
						<tr key={emp.empId}>
							<td>{emp.firstName}</td>
							<td>{emp.lastName}</td>
							<td>{emp.emailId}</td>
							<td>
								<Button variant="info" className="m-1 mt-0 mb-0">
									Update
								</Button>
								<Button variant="danger" className="m-1 mt-0 mb-0">
									Delete
								</Button>
								<Button variant="info" className="m-1 mt-0 mb-0">
									View
								</Button>
							</td>
						</tr>
					))}
				</tbody>
			</Table>
		</div>
	);
}

export default EmployeeList;
